import java.util.*;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		
		int inum1 = 0;
		int inum2 = 0;
		
		inum1 = sc.nextInt();
		inum2 = sc.nextInt();
		
		System.out.println(inum1+inum2);
		System.out.println(inum1-inum2);
		System.out.println(inum1*inum2);
		System.out.println(inum1/inum2);	
		System.out.println(inum1%inum2);
	}
	
}